import React, { useState } from "react";

const Counter = () => {
	const [count,setCount] = useState(0);
	const [tags,setTags] = useState(['tag1','tag2','tag3']);
	const formatCount = () => {
		return count === 0 ? 'empty' : count;
	};
	const getBadgeClasses = () => {
	let classes = 'badge m-2 '
	return classes+= count === 0 ? 'bg-warning' : 'bg-primary';
	}

	const handleIcrement = () => {
		setCount((prevState) =>prevState + 1)
	}

	const handleDecrement = () => {
		setCount((prevState) =>prevState - 1)
	}
	
	return (
		<>
		<span className={getBadgeClasses()}>{formatCount()}</span>
		<button className="btn btn-primary btn-sm" onClick={handleIcrement}>+</button>
		<button className="btn btn-primary btn-sm" onClick={handleDecrement}>-</button>
		</>
	);
}

export default Counter;
