const Counter = (props) => {
	const {value} = props
	const formatValue = () => {
		return value === 0 ? 'empty' : value;
	};
	const getBadgeClasses = () => {
	let classes = 'badge m-2 '
	return classes+= value === 0 ? 'bg-warning' : 'bg-primary';
	}

	const handleIcrement = () => {
		// setValue((prevState) =>prevState + 1)
		console.log('handleIncremret');
	}

	const handleDecrement = () => {
		console.log('handleDecremret');
		
		// setValue((prevState) =>prevState - 1)
	}

	return (
		<div>
		<span>{props.name}</span>
		<span className={getBadgeClasses()}>{formatValue()}</span>
		<button className="btn btn-primary btn-sm m-2" onClick={() => props.onIncrement(props.value)}>+</button>
		<button className="btn btn-primary btn-sm m-2" onClick={() => props.onDecrement(props.value)}>-</button>
		<button className="btn btn-danger btn-sm m-2" onClick={() => props.onDelete(props.id)}>Delete</button>
		</div>
	);
}

export default Counter;
